def generated_loss(batch, model_output, extra):
    alpha = extra.get("alpha", 1.0)
    logit_diff = alpha * (batch["log_prob_w"] - batch["log_prob_l"])
    loss = -extra["ops"].logsigmoid(logit_diff)
    weight = batch.get("weight")
    if weight is not None:
        loss = loss * weight
    return loss.mean()
